$(function(){
    $(".window").find("div").hide();    //hidden all contents in nav
})

$("ul li a").click(function () {
    if (!$(this).hasClass("touchOn")) {
        $(this).addClass("touchOn");
    }
    $(this).siblings().removeClass("touchOn");
    var id = $(this).attr('id');    // get .text id
    id = id.slice(0, 1);            // get the corresponding id     
    var showId = $(".window").find("div").eq((id - 2)).attr('id'); 
    showId='#'+showId;
    $(showId).show();
    $(showId).siblings().hide();
    $(".home_page").hide();
})
